import {React, useEffect, useState}from "react";
import { Formik, Form, Field, ErrorMessage, useField } from "formik";
import * as Yup from 'yup'
import axios from "axios";

const database_id = '58d96ae9275547a7960f5cca7c93e836'

const CustomTextInput = ({label, ...props}) => {
  const [field, meta] = useField(props)

  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <input className="text-input" {...field}{...props} />
      {meta.touch && meta.error ? (
        <div className="error">{meta.error}</div> ) : null}
    </>
  )
}

const CustomCheckbox = ({children, ...props}) => {
  const [field, meta] = useField(props, 'checkbox')

  return (
    <>
      <label className="checkbox">
        <input type="checkbox" {...field}{...props} />
        {children}
      </label>
      {meta.touch && meta.error ? (
        <div className="error">{meta.error}</div> ) : null}
    </>
  )
}

const CustomSelect = ({label, ...props}) => {
  const [field, meta] = useField(props)

  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <select {...field}{...props} />
      {meta.touch && meta.error ? (
        <div className="error">{meta.error}</div> ) : null}
    </>
  )
}

function CreateReport() {
    const [project, setProject] = useState([])
    useEffect(()  => {
     axios.post("http://localhost:3001/getproject").then((response) => {
        setProject(response.data)
        
      }) 
      
    }, [])

    console.log(project)
    
    const onSubmit = (data) => {

        fetch('http://localhost:3001/createtimereports',{
        method: 'POST',
        url: 'https://api.notion.com/v1/pages',
        body: (
          JSON.stringify(data)),
        headers: { "Content-Type": 'application/json' }
      }) 
       console.log(JSON.stringify(data))
      
    };

  return (
    <div className='createReportPage'>
      <Formik
        initialValues={{
          person: "",
          project: "",
          date: "",
          hours: "",
          note:"",
          comment:""
        }}
        validationSchema={
          Yup.object().shape({
          person: Yup.string().required(),
          project: Yup.string().required(),
          date: Yup.date().required(),
          hours:Yup.number().required(),
          note: Yup.string().required(),
          comment: Yup.string().required()
        })}
        onSubmit={(values, {setSubmitting, resetForm}) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2))
            resetForm();
            setSubmitting(false)
          }, 2000)
        }}
        >
        {props => (
        <Form className="container">
          <h1></h1>
          <label className="input">Person:</label>
          <ErrorMessage name="Person"/>
          <Field
            id='inputCreateReport'
            name='person'
            placeholder='(Ex. Oliver....)'
          />
          <label className="input">Project:</label>
          <Field name="project" as='select'>
            <option value="hello">hej</option>   
          </Field>   
          <label className="input">Date:</label>
          <Field
            id='inputCreateReport'
            type="date"
            name='date'
            placeholder='(Ex. 2022-11-13...)'
          />
          <label className="input">Hours:</label>
          <Field
            id='inputCreateReport'
            name='hours'
            placeholder='(Ex. 11...)'
          />
          <label className="input">Note:</label>
          <Field
            id='inputCreateReport'
            name='note'
            placeholder='(Ex. meeting....)'
          />
          <label className="input">Comment:</label>
          <Field
            
            id='inputCreateReport'
            name='comment'
            placeholder='(Ex. fixed this bug....)'
          />

          <button type='submit' className="button">{props.isSubmitting ? 'loading...' : 'Submit'}</button>
        </Form>
        )}
      </Formik>
    </div>
  );
}

export default CreateReport;
