import React from 'react'
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend
} from "chart.js"
import {Bar, Line} from 'react-chartjs-2'

ChartJS.register(
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend
)

export const BarChart = ({data, options}) => {
    useEffect(() => {
        setChartData({
            labels:["v2", "v3", "v4", "v5", "v6"],
            datasets: [
                {
                    label: "Arbetade timmar",
                    data: [180, 60, 340, 145, 130],
                    borderColor: "rgb(53, 162, 235)",
                    backgroundColor: "rgba(53, 162, 235, 0.4)"
                }
            ]
        })
        setChartOptions({
            responsive: true,
            plugins: {
                Legend: {
                    position: "top"
                },
                Title: {
                    display: true,
                    text: "Hours worked"
                }
            }
        })
      }, [])
       
    return (
        <div>
            <Bar options={options} data={data} />
        </div>
    )
}
