import React, {useState, useEffect} from 'react'
import axios from 'axios'

function Home() {
    const [reports, setReports] = useState([]);

    useEffect( () => {
      axios.post("http://localhost:3001/getdatabase/report").then((response) => {
        setReports(response.data)
      }) 
    }, [])

    

  
  return (
    <div>
      {console.log(reports)}
      {
        reports.map((value) => {
          return(
            <div className='report-card'>
              <ul>
                <li key={value.id}>Person: {value.personName}</li>
                <li key={value.id}>Project: {value.projectName}</li>
                <li key={value.id}>Date: {value.date}</li>
                <li key={value.id}>Hours:{value.hours}</li>
                <li key={value.id}>Note: {value.note}</li>
                <li key={value.id}>Comment: {value.properties.Comment.rich_text[0].plain_text}</li>
                
              </ul>
              <button>Edit Timereport</button>
              <button type='myButton'>Add Comment</button>
              
            </div>
          )
        })
      }   
    </div>
  )
}

export default Home
