import React, { useState, useEffect} from 'react'
import { useNavigate } from 'react-router-dom'
import { Formik, Form } from 'formik'
import * as Yup from 'yup'
import axios from 'axios'
import './Project.css'

import CustomTextInput from '../Components/CustomTextInput'
import CustomSelect from '../Components/CustomSelect'

import Navbar from './Navbar'


function Project() {
    const navigate = useNavigate();
    const [project, setProject] = useState([])

    axios.post('http://localhost:3001/project', {data: ""},
    { headers: {accessToken: sessionStorage.getItem("accessToken")}}).then((response) => {
        console.log(response.data)
    })

    useEffect(()  => {
        axios.post("http://localhost:3001/project/getprojects", {data: ""}, { headers: {
         accessToken: sessionStorage.getItem("accessToken")
       }}).then((response) => {
           setProject(response.data)
           console.log(response)
           
         }) 
   
       }, [])
  return (
    <>
        <Navbar />
        <div className='project-page'>  
        <Formik
          initialValues={{
            
            project: "",
            date: "",
            hours: "",
            note:"",
            comment:""
          }}
          validationSchema={
            Yup.object().shape({
            project: Yup.string().required(),
            date: Yup.date().required(),
            hours:Yup.number().required(),
            note: Yup.string().required(),
            comment: Yup.string().required()
          })}
          onSubmit={(values, {setSubmitting, resetForm}) => {
            axios
              .post('http://localhost:3001/createtimereports', 
                values,
                {
                  headers: {
                    accessToken: sessionStorage.getItem("accessToken")
                  }
                }
              )
              
              setTimeout(() => {
                navigate("/home")
                resetForm();
                setSubmitting(false)
              }, 2000)
              
              
            
            
          }}
          >
          {props => (
          <Form className="">
            <h1>Project</h1>
            <CustomSelect className="" label="Project" name="project">
              <option value="">Select Project</option>  
              {
                project.map((pages) => {
                  return (
                    <>
                      <option value={pages.id}>{pages.name}</option> 
                    </>
                  )
                  
                })
              }
            </CustomSelect>   
            <CustomTextInput className="" label="From: " name="date" type="date"/>
            <CustomTextInput className="" label="To: " name="date" type="date"/>
            
            
            

            <button type='submit' className="">{props.isSubmitting ? 'loading...' : 'Submit'}</button>
          </Form>
          )}
        </Formik>  
        </div>
    </>
  )
}

export default Project
